gsap.registerPlugin(ScrollTrigger),gsap.from("#arrow",{duration:2,alpha:0,yPercent:-100,scrollTrigger:{trigger:"#arrow",start:"top 80%",end:"bottom 80%",toggleActions:"restart none none none"}});const cards=document.querySelectorAll(".card");for(var i=0;i<cards.length;i++){cardFlip(cards[i])}function cardFlip(r){r.addEventListener("click",(function(){r.classList.toggle("is-flipped")}))}